class Gadget {}
Gadget.getCaller = function getCaller() {
  let callerName;
  try {
    throw new Error();
  } catch (e) {
    const re = /(\w+)@|at (\w+) \(/g;
    const st = e.stack;
    const m = re.exec(st);
    re.exec(st);
    callerName = m[1] || m[2];
  }

  return callerName;
};

Gadget.overrideConsole = function overrideConsole() {
  window.console = (function hookConsole(oldCons) {
    const exConsole = {};
    Object.keys(oldCons).forEach((key) => {
      exConsole[key] = function action(...args) {
        oldCons[key]('123', ...args).bind(oldCons);
      };
    });
    return exConsole;
  }(window.console));
};
